import { Component, OnInit } from '@angular/core';
import { ProductsModel } from './models-search-invoice';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { integerValidator } from '../utils';
import { ApiService } from '../api.service';
import { CustomerESModel } from '../create-invoice/models-invoice';

@Component({
  selector: 'app-search-invoice-component',
  templateUrl: './search-invoice.component.html',
  styleUrls: ['./search-invoice.component.css']
})
export class SearchInvoiceComponent implements OnInit{
  constructor(private apiService: ApiService) {}
  customers: CustomerModel[] = [];
  products: ProductsModel[] = [];

  myForm = new FormGroup({
    invoiceNumber: new FormControl("", [Validators.required, Validators.min(1), integerValidator()]),
    customerId: new FormControl("", [Validators.required])
  });

  ngOnInit() {
    this.getCustomer();
  }

  getCustomer() {
    this.apiService.get('customer').subscribe((data: CustomerESModel[]) => {
      this.customers = data.map((product: CustomerESModel) => {
        return {
          id: product.id,
          businessName: product.razonSocial,
        };
      });
    });
  }
  
  public test() {
  }



  formatPrice(price: number): string {
    return price.toLocaleString('es-CO', { style: 'decimal', minimumFractionDigits: 2, maximumFractionDigits: 2 });
  }



}
<div class="container">
  <div class="search-section">
    <label for="search-type">Tipo de Búsqueda:</label>
    <input type="radio" id="cliente" name="search-type" value="activo" checked>
    <label for="cliente" >Cliente</label>
    <input type="radio" id="numero-factura" name="search-type">
    <label for="numero-factura">Número de Factura</label>
  </div>
  <div class="customer-info">
    <form [formGroup]="myForm">
      <div class="field">
        <label>Cliente</label>
        <select formControlName="customerId">
          <option value="">Seleccionar Cliente</option>
          <option *ngFor="let customertOption of customers" [value]="customertOption.id">{{customertOption.businessName}}</option>
        </select>
        <div class="required" *ngIf="myForm.get('customerId')?.hasError('required')">
          Este campo es obligatorio.
        </div>
      </div>
      <div class="field">
        <label>Numero de Factura</label>
        <input formControlName="invoiceNumber" type="text">
        <div class="required" *ngIf="myForm.get('invoiceNumber')?.hasError('required')">
          Este campo es obligatorio.
        </div>
        <div class="required" *ngIf="myForm.get('invoiceNumber')?.hasError('notInteger')">
          Solo se permiten numeros enteros.
        </div>
      </div>
    </form>
  </div>
  <table class="products">
    <thead>
      <tr>
        <th>Numero de factura</th>
        <th>Fecha Emision</th>
        <th>Total facturado</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let product of products">
        <td>
          {{product.number}}
        </td>
        <td>
          {{product.date}}
        </td>
        <td>
          {{formatPrice(product.total)}}
        </td>
      </tr>
    </tbody>
  </table>
</div>
