import { Component, OnInit } from '@angular/core';
import Swal from 'sweetalert2';
import { ApiService } from '../api.service';
import { CartProductModel, FixedProductModel, ProductsESModel, CustomerESModel, CustomerModel } from './models-invoice';
import { FormControl, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-create-invoice',
  templateUrl: './create-invoice.component.html',
  styleUrls: ['./create-invoice.component.css']
})
export class CreateInvoiceComponent implements OnInit{
  constructor(private apiService: ApiService) {}

  fixedProducts: FixedProductModel[] = [];
  customers: CustomerModel[] = [];

  //Datos
  cartProducts: CartProductModel[] = []; 

  myForm = new FormGroup({
    invoiceNumber: new FormControl(0, [Validators.required, Validators.min(0)])
  });

  ngOnInit() {
    this.getCustomer();
    this.getProductos();
  }

  getProductos() {
    this.apiService.get('products').subscribe((data: ProductsESModel[]) => {
      this.fixedProducts = data.map((product: ProductsESModel) => {
        return {
          id: product.id,
          name: product.nombreProducto,
          unitPrice: product.precioUnitario,
          imageUrl: product.imagenProducto
        };
      });
    });
  }

  getCustomer() {
    this.apiService.get('customer').subscribe((data: CustomerESModel[]) => {
      this.customers = data.map((product: CustomerESModel) => {
        return {
          id: product.id,
          businessName: product.razonSocial,
        };
      });
    });
  }

  

  clearCart() {
    if (this.cartProducts.length > 0) {
      Swal.fire({
        title: 'Limpiar Carrito',
        text: '¿Estás seguro de que deseas eliminar todos los productos del carrito?',
        icon: 'warning',
        showCancelButton: true,
        confirmButtonText: 'Sí, Limpiar',
        cancelButtonText: 'Cancelar'
      }).then((result) => {
        if (result.isConfirmed) {
          this.cartProducts = [];
          Swal.fire('Carrito limpiado', '', 'success');
        }
      });
    } else {
      Swal.fire('Carrito Vacío', 'El carrito ya está vacío.', 'info');
    }
  }

  addProductToCart() {
    this.cartProducts.push({ quantity: 1, total: 0 });
  }

  calculateSubtotal() {
    let subtotal = 0;
    for (const cartProduct of this.cartProducts) {
      if (cartProduct.product != null) {
        subtotal += cartProduct.product.unitPrice * cartProduct.quantity;
      }
    }
    return subtotal;
  }

  calculateTaxes() {
    return this.calculateSubtotal() * 0.19;
  }

  calculateTotal() {
    return this.calculateSubtotal() + this.calculateTaxes();
  }

  onProductSelected(event: any, producto: CartProductModel) {
    producto.total = 0;
    const selectedProductId = parseInt(event.target.value, 10);
    const selectedProduct = this.fixedProducts.find(product => product.id === selectedProductId);
    producto.product = selectedProduct;
    producto.total = producto.quantity * selectedProduct!.unitPrice;
  }

  changeQuantity(event: any, producto: CartProductModel) {
    const quantity = parseInt(event.target.value, 10);
    producto.quantity = quantity;
    if (producto.product != null ) {
      producto.total = quantity * producto.product.unitPrice;
    }
  }

  formatPrice(price: number): string {
    return price.toLocaleString('es-CO', { style: 'decimal', minimumFractionDigits: 2, maximumFractionDigits: 2 });
  }

  saveInvoice() {
    // Implement logic to save the invoice
    // You can send the invoice data to a server or perform any necessary actions here
  }
}

<div class="container">
  <button (click)="clearCart()" class="new-voucher-button">Nueva factura</button>

  <div class="customer-info">
    <form [formGroup]="myForm">
      <div class="field">
        <label>Cliente</label>
        <select >
          <option value="0">Seleccionar Cliente</option>
          <option *ngFor="let customertOption of customers" [value]="customertOption.id">{{customertOption.businessName}}</option>
        </select>
      </div>
      <div class="field">
        <label>Numero de Factura</label>
        <input formControlName="invoiceNumber" type="number">
      </div>
    </form>
  </div>
  <button (click)="addProductToCart()" class="new-button">Agregar producto</button>
  <table class="products">
    <thead>
      <tr>
        <th>Prodcuto</th>
        <th>Precio Unitario</th>
        <th>Cantidad</th>
        <th>Imagen</th>
        <th>Totales</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let product of cartProducts">
        <td>
          <select (change)="onProductSelected($event, product)">
            <option value="0">Seleccionar Producto</option>
            <option *ngFor="let productOption of fixedProducts" [value]="productOption.id">{{productOption.name}}</option>
          </select>
        </td>
        <td>
          {{product.product ? formatPrice(product.product!.unitPrice): 0}}
        </td>
        <td>
          <input (change)="changeQuantity($event, product)" type="number" step="1" value="1" min="0" >
        </td>
        <td>
          <img [src]="product.product ? product.product!.imageUrl : ''" [alt]="product.product ? product.product!.name : ''">
        </td>
        <td>
          {{formatPrice(product.total)}}
        </td>
      </tr>
    </tbody>
  </table>

  <table class="summary">
    <tr>
      <td>Sub total</td>
      <td>{{formatPrice(calculateSubtotal())}}</td>
    </tr>
    <tr>
      <td>Impuestos (19%)</td>
      <td>{{formatPrice(calculateTaxes())}}</td>
    </tr>
    <tr>
      <td>Total</td>
      <td>{{formatPrice(calculateTotal())}}</td>
    </tr>
  </table>

  <button (click)="saveInvoice()" class="save-button">Guardar</button>
</div>
